NSERT INTO `PodCasts_show` (`id`, `Podcast_id`, `Title`, `GUID`, `Link`, `MediaSize`, `MediaURL`, `MediaType`, `Description`, `PubDate`) VALUES
(1, 1, 'NPR: 06-01-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/187882663/npr_187882663.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 23264381, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/187882663/npr_187882663.mp3', 'audio/mpeg', 'Stories:  1) Who''s Bill This Time 2) Panel Round One 3) Bluff The Listener 4) Not My Job: Deepak Chopra Plays ''Yes, We Cannes'' 5) Extra Extra! 6) Panel Round Two 7) Limericks 8) Lightning Fill In The Blank 9) Prediction', '2013-06-01'),
(2, 1, 'NPR: 05-25-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/186614587/npr_186614587.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 23321930, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/186614587/npr_186614587.mp3', 'audio/mpeg', 'Stories:  1)  Who''s Bill This Time 2) Panel Round One 3) Bluff The Listener 4) NFL Sideline Reporter Michele Tafoya Plays Not My Job 5) Panel Round Two 6) Limericks 7) Lightning Fill In The Blank 8) Prediction', '2013-05-25'),
(3, 1, 'NPR: 05-18-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/185118162/npr_185118162.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 23339484, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/185118162/npr_185118162.mp3', 'audio/mpeg', 'Stories:  1) Who''s Bill This Time 2) Panel Round One 3) Bluff The Listener 4) Not My Job: Three Headless Chicken Questions For Alice Cooper 5) Panel Round Two 6) Limericks 7) Lightning Fill In The Blank 8) Prediction', '2013-05-18'),
(4, 1, 'NPR: 05-11-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/183189933/npr_183189933.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 23250875, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/183189933/npr_183189933.mp3', 'audio/mpeg', 'Stories:  1) Who''s Bill This Time 2) Panel Round One 3) Bluff The Listener 4) Google Chairman Eric Schmidt Plays Not My Job 5) Panel Round Two 6) Limericks 7) Lightning Fill In The Blank 8) Prediction', '2013-05-11'),
(5, 1, 'NPR: 05-04-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/181170001/npr_181170001.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 23190192, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/181170001/npr_181170001.mp3', 'audio/mpeg', 'Stories:  1) Who''s Carl This Time 2) Panel Round One 3) Bluff The Listener 4) Not My Job: Steve Martin Takes A Quiz About Boring People 5) Music Interlude 6) Panel Round Two 7) Limericks 8) Lightning Fill In The Blank 9) Prediction', '2013-05-04'),
(6, 1, 'NPR: 04-27-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/179495735/npr_179495735.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 100000, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/179495735/npr_179495735.mp3', 'audio/mpeg', 'Stories:  1) Who''s Carl This Time? 2) Panel Round One 3) Bluff The Listener 4) Not My Job: Kal Penn Takes A Quiz On The Microbiome 5) Panel Round Two 6) Limericks 7) Lightning Fill In The Blank 8) Prediction', '2013-04-27'),
(7, 1, 'NPR: 04-20-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/178149051/npr_178149051.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 100000, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/178149051/npr_178149051.mp3', 'audio/mpeg', 'Stories:  1) Actor Jeff Bridges Plays Not My Job 2) Secretary Of Education Arne Duncan Plays Not My Job 3) Al Gore Plays Not My Job: Extended Cut 4) Singer Erykah Badu Plays Not My Job 5) Melinda Gates Plays Not My Job', '2013-04-20'),
(8, 1, 'NPR: 04-13-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/177161023/npr_177161023.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 100000, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/177161023/npr_177161023.mp3', 'audio/mpeg', 'Stories:  1) Who''s Carl This Time? 2) Panel Round One 3) Bluff The Listener 4) Not My Job: Food Writer Mark Bittman Takes A Quiz About Batman 5) Panel Round Two 6) Limericks 7) Lightning Fill In The Blank 8) Prediction', '2013-04-13'),
(9, 1, 'NPR: 04-06-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/176447296/npr_176447296.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 100000, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/176447296/npr_176447296.mp3', 'audio/mpeg', 'Stories:  1) Who''s Carl This Time? 2) Panel Round One 3) Bluff The Listener 4)  Rick Nielsen Of Cheap Trick Plays Not My Job 5) Panel Round Two 6) Limericks 7) Lightning Fill In The Blank 8) Prediction', '2013-04-06'),
(10, 1, 'NPR: 03-30-2013 Wait Wait... Don''t Tell Me!', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/175796767/npr_175796767.mp3', 'http://www.npr.org/templates/rundowns/rundown.php?prgId=35', 100000, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/35/175796767/npr_175796767.mp3', 'audio/mpeg', 'Stories:  1) Who''s Carl This Time? 2) Panel Round One 3) Bluff The Listener 4) R&B Singer Aaron Neville Plays Not My Job 5) Panel Round Two 6) Limericks 7) Lightning Fill In The Blank 8) Prediction', '2013-03-30'),
(11, 5, 'Broadcasting the Woolwich Video, George Plimpton''s Legacy and More', 'http://www.onthemedia.org/2013/may/31/', 'http://feeds.wnyc.org/~r/onthemedia/~3/hCPQsTgzg-g/', 37778314, 'http://feeds.wnyc.org/~r/onthemedia/~5/0hQmNO8aapY/otm053113pod.mp3', 'audio/mpeg', '<p><span><span><span>The difficulty of reporting on acts of terror, looking back at a giant of participatory journalism, and a blogger who  learned to love his online tormentor.<br></span></span></span></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/hCPQsTgzg-g" height="1" width="1"/>', '2013-05-31'),
(12, 5, 'A Week of Scandals, Catholic Whistleblowers, and More', 'http://www.onthemedia.org/2013/may/24/', 'http://feeds.wnyc.org/~r/onthemedia/~3/6zUGkFwQYKg/', 37418458, 'http://feeds.wnyc.org/~r/onthemedia/~5/XCaqgFPaPhU/otm052413pod.mp3', 'audio/mpeg', '<p>Looking at the media''s governmental scandal narrative, an American blog rocks Canadian politics, and whistleblowers in the Catholic Church.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/6zUGkFwQYKg" height="1" width="1"/>', '2013-05-24'),
(13, 5, 'Conservative Bloggers Vindicated, Advice for Leakers, and More', 'http://www.onthemedia.org/2013/may/17/', 'http://feeds.wnyc.org/~r/onthemedia/~3/AzeZdgWees4/', 38085034, 'http://feeds.wnyc.org/~r/onthemedia/~5/TpeIKqrssIk/otm051713pod.mp3', 'audio/mpeg', '<p><span>Some vindication for conservative bloggers in the IRS scandal, advice for sources after the AP call-record seizure, the Bloomberg Terminal scandal, and what the people thought the newspaper industry would look like in the future.</span></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/AzeZdgWees4" height="1" width="1"/>', '2013-05-17'),
(14, 5, 'Who.s gonna pay for this stuff?', 'http://www.onthemedia.org/2013/may/10/', 'http://feeds.wnyc.org/~r/onthemedia/~3/kuh94xBj2VQ/', 37777018, 'http://feeds.wnyc.org/~r/onthemedia/~5/Vq66Twl3EhA/otm051013pod.mp3', 'audio/mpeg', '<p>This week, a special hour on the incredible volume of media available to consumers, and the incredible difficulty of making money for creators.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/kuh94xBj2VQ" height="1" width="1"/>', '2013-05-10'),
(15, 5, 'Jason Collins Comes Out, Patenting Genes And More', 'http://www.onthemedia.org/2013/may/03/', 'http://feeds.wnyc.org/~r/onthemedia/~3/ZIA2Qf7GuoA/', 38069309, 'http://feeds.wnyc.org/~r/onthemedia/~5/KtOku7jNXn8/otm050313pod.mp3', 'audio/mpeg', '<p>A look at the media fallout around NBA center Jason Collins'' announcement that he is gay, how one company is defending its patent of two genes linked to breast cancer, and how filing a Freedom of Information Act request just got a lot more complicated.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/ZIA2Qf7GuoA" height="1" width="1"/>', '2013-05-03'),
(16, 5, 'Surveillance After The Marathon Bombing, The Kill Team and More', 'http://www.onthemedia.org/2013/apr/26/', 'http://feeds.wnyc.org/~r/onthemedia/~3/n6Pd_Tbwb-w/', 38127371, 'http://feeds.wnyc.org/~r/onthemedia/~5/_okS55PsXxM/otm042613pod.mp3', 'audio/mpeg', '<p>Bob looks at the risks and promise of surveillance after the Boston marathon bombing, a new documentary looks at war crimes and whistleblowing in the theater of war and an Onion-like satire site tries to bring military humor to the civilian masses.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/n6Pd_Tbwb-w" height="1" width="1"/>', '2013-04-26'),
(17, 5, 'Coverage Of The Boston Bombing, Undercover Reporting, and MORE', 'http://www.onthemedia.org/2013/apr/19/', 'http://feeds.wnyc.org/~r/onthemedia/~3/fncriX_7xQ8/', 37802516, 'http://feeds.wnyc.org/~r/onthemedia/~5/p7zJipmHA3Q/otm041913pod.mp3', 'audio/mpeg', '<p>Brooke examines the twists and turns of the marathon bombing coverage, listeners tell OTM what they want to hear from the media when there''s no new information during a crisis, and what it''s like to tweet the news cycle in the wee small hours of the morning.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/fncriX_7xQ8" height="1" width="1"/>', '2013-04-19'),
(18, 5, 'Gun Anecdotes, What To Know about the Avian Flu, and Grindr.', 'http://www.onthemedia.org/2013/apr/12/', 'http://feeds.wnyc.org/~r/onthemedia/~3/Nl9gvf9TM9Y/', 37901003, 'http://feeds.wnyc.org/~r/onthemedia/~5/CUEJ1NJP60Q/otm041213pod.mp3', 'audio/mpeg', '<p>Since the Sandy Hook shootings, gun violence anecdotes have been the centerpiece of much of the reporting about guns. OTM asks if there''s a better way. Also, who to listen to about the new avian flu and everything you need to know about Grindr.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/Nl9gvf9TM9Y" height="1" width="1"/>', '2013-04-12'),
(19, 5, 'The Future Of Egyptian Media, the Bitcoin Bubble, and More', 'http://www.onthemedia.org/2013/apr/05/', 'http://feeds.wnyc.org/~r/onthemedia/~3/qaFx8R5vANM/', 37919578, 'http://feeds.wnyc.org/~r/onthemedia/~5/5U1J7di5TcI/otm040513pod.mp3', 'audio/mpeg', '<p>After the arrest (and release) of Bassem Youssef, OTM looks back on a trip on a 2011 trip to Egypt and forward to the future of independent Egyptian media. Also: the song remains the same in North Korea coverage and innovative TV ads from Old Milwaukee.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/qaFx8R5vANM" height="1" width="1"/>', '2013-04-05'),
(20, 5, 'Culture and the Courts, The Legacy of Rand Paul''s Filibuster, and More', 'http://www.onthemedia.org/2013/mar/29/', 'http://feeds.wnyc.org/~r/onthemedia/~3/pDG9yGHM8Uo/', 37446971, 'http://feeds.wnyc.org/~r/onthemedia/~5/BQt05PKBAe4/otm032913pod.mp3', 'audio/mpeg', '<p>How Popular Culture Influences SCOTUS Decisions, How Rand Paul''s Drone Filibuster May Have Changed Minds, and Brooke talks with the great Walt "Clyde" Frazier about sports and the media.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/pDG9yGHM8Uo" height="1" width="1"/>', '2013-03-29'),
(21, 5, 'Rape coverage after Steubenville, the false promise of the personal finance industry and more.', 'http://www.onthemedia.org/2013/mar/22/', 'http://feeds.wnyc.org/~r/onthemedia/~3/9CBZ83dCLB8/', 50411837, 'http://feeds.wnyc.org/~r/onthemedia/~5/SKOgQUik0kY/otm032213pod.mp3', 'audio/mpeg', '<p>How the media is covering yet another high school rape case after Steubenville, how personal finance luminaries lead the public astray, and an infamous hacker threatens to tarnish the public image of all hackers.</p><p></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/9CBZ83dCLB8" height="1" width="1"/>', '2013-03-22'),
(22, 5, '10 Years After The Iraq War and Big Threat to Whistleblowers', 'http://www.onthemedia.org/2013/mar/15/', 'http://feeds.wnyc.org/~r/onthemedia/~3/wTnlmroVdxc/', 44170605, 'http://feeds.wnyc.org/~r/onthemedia/~5/jadzwkMfM_Y/otm031513pod.mp3', 'audio/mpeg', '<p>This week On the Media examines the  .aiding the enemy. charge the government has brought against Bradley Manning,  the man who gave hundreds of thousands of classified documents to WikiLeaks.  Also, 10 years after the Iraq  War began Brooke finds out what.s happened to three Iraqi journalists profiled  on the show in 2006.</p><p></p><p></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/wTnlmroVdxc" height="1" width="1"/>', '2013-03-15'),
(23, 5, 'The Past, Present, and Future of Ownership', 'http://www.onthemedia.org/2013/mar/08/', 'http://feeds.wnyc.org/~r/onthemedia/~3/eB-B2Lbdvvg/', 37446970, 'http://feeds.wnyc.org/~r/onthemedia/~5/hR-IWSyj7Ms/otm030813pod.mp3', 'audio/mpeg', '<p>A special hour on our changing understanding of ownership and how it is affected by the law. An author and professor who encourages creative writing through plagiarism, 3D printing, fan fiction&amp; fair use, and the strange tale of who owns "The Happy Birthday Song"</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/eB-B2Lbdvvg" height="1" width="1"/>', '2013-03-08'),
(24, 5, 'Frustration in the White House Press Corps and More', 'http://www.onthemedia.org/2013/mar/01/', 'http://feeds.wnyc.org/~r/onthemedia/~3/-CqAX1le6oY/', 37446969, 'http://feeds.wnyc.org/~r/onthemedia/~5/ml8J4muEYxY/otm030113pod.mp3', 'audio/mpeg', '<p>Why White House reporters are fed up, getting the president''s attention through online petitions, and a conversation with<em>The Daily Show''s</em>Aasif Mandvi.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/-CqAX1le6oY" height="1" width="1"/>', '2013-03-01'),
(25, 5, 'The Latest Federal Budget Crisis, Predicting the Oscars, and More', 'http://www.onthemedia.org/2013/feb/22/', 'http://feeds.wnyc.org/~r/onthemedia/~3/-BDIQ4L_GME/', 37446971, 'http://feeds.wnyc.org/~r/onthemedia/~5/5WqLVVhTDrI/otm022213pod.mp3', 'audio/mpeg', '<p>Why federal budget crises seem to happen every month, covering the Vatican from a world away, and using election prediction models to forecast the winners of the 85th Academy Awards.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/-BDIQ4L_GME" height="1" width="1"/>', '2013-02-22'),
(26, 5, 'Truman Capote''s Lies, Cybersecurity and More', 'http://www.onthemedia.org/2013/feb/15/', 'http://feeds.wnyc.org/~r/onthemedia/~3/8uvUCdZRoIc/', 37446971, 'http://feeds.wnyc.org/~r/onthemedia/~5/C2KEY7n5d3s/otm021513pod.mp3', 'audio/mpeg', '<p>How Truman Capote mixed fact and fiction in<em>In Cold Blood</em>, the true threat of cyberwar, and software that fact-checks political speeches in real time.</p><p></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/8uvUCdZRoIc" height="1" width="1"/>', '2013-02-15'),
(27, 5, 'The First Viral Video, New Drone Disclosures, and Reclaiming the Word Jihad.', 'http://www.onthemedia.org/2013/feb/08/', 'http://feeds.wnyc.org/~r/onthemedia/~3/b3oEkWTchNY/', 37446970, 'http://feeds.wnyc.org/~r/onthemedia/~5/28TgwlC77tk/otm020813pod.mp3', 'audio/mpeg', '<p>New disclosures begin to put the drone program into focus, a social-media campaign to reclaim the word jihad from extremists, and the story of the very first viral video.</p><p></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/b3oEkWTchNY" height="1" width="1"/>', '2013-02-08'),
(28, 5, 'Hacking the New York Times, Tweeting Revolutions, and More', 'http://www.onthemedia.org/2013/feb/01/', 'http://feeds.wnyc.org/~r/onthemedia/~3/K2ZmMuZPG9U/', 37434874, 'http://feeds.wnyc.org/~r/onthemedia/~5/-bsNDYPowMA/otm020113pod.mp3', 'audio/mpeg', '<p>How<em>The New York Times</em>fell victim to Chinese hackers, a survey of our digital file sharing habits, and a conversation with NPR senior strategist Andy Carvin on tweeting revolutions.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/K2ZmMuZPG9U" height="1" width="1"/>', '2013-02-01'),
(29, 5, 'The Church of Scientology, Facebook Goes Searching, and More', 'http://www.onthemedia.org/2013/jan/25/', 'http://feeds.wnyc.org/~r/onthemedia/~3/ik1M-rkb4-I/', 37433579, 'http://feeds.wnyc.org/~r/onthemedia/~5/h7Mobbrborw/otm012513pod.mp3', 'audio/mpeg', '<p>On this week.s<em>On the  Media</em>, Lawrence Wright on his in-depth investigation into the  Church of  Scientology, The Autism Channel, and Facebook''s new search feature.</p><p></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/ik1M-rkb4-I" height="1" width="1"/>', '2013-01-25'),
(30, 5, 'Violent Video Games, Lying Athletes, and More', 'http://www.onthemedia.org/2013/jan/18/', 'http://feeds.wnyc.org/~r/onthemedia/~3/b1hyEfBD-Os/', 36525515, 'http://feeds.wnyc.org/~r/onthemedia/~5/FX2FHUOZMCM/otm011813pod.mp3', 'audio/mpeg', '<p>The history of studies on video games and aggression, a reporter''s coverage of every underage gun death in New York City, Lance Armstrong, Manti Te''o, and remembering Aaron Swartz.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/b1hyEfBD-Os" height="1" width="1"/>', '2013-01-18'),
(31, 5, 'Mapping Gun Owners, International Journalists Fight Censorship, and More', 'http://www.onthemedia.org/2013/jan/11/', 'http://feeds.wnyc.org/~r/onthemedia/~3/cMCwb_PKE4Y/', 37446971, 'http://feeds.wnyc.org/~r/onthemedia/~5/iszmmdGrLuk/otm011113pod.mp3', 'audio/mpeg', '<p>The ATF''s desire for a central database of gun transactions, journalists fight for the right to report on India''s rape trial, an interview with<em>56 Up</em>director Michael Apted, and Chinese journalists strike after the government censored an  Op-Ed.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/cMCwb_PKE4Y" height="1" width="1"/>', '2013-01-11'),
(32, 5, 'The Privacy Show', 'http://www.onthemedia.org/2013/jan/04/', 'http://feeds.wnyc.org/~r/onthemedia/~3/AITknZNJ-yU/', 37444810, 'http://feeds.wnyc.org/~r/onthemedia/~5/fdbSfyMJN7Y/otm010413pod.mp3', 'audio/mpeg', '<p>A special hour on privacy - license plate readers, national security letters, surveilling yourself so the government doesn''t have to, and OTM producer Sarah Abdurrahman on just how much we misunderstand our privacy online.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/AITknZNJ-yU" height="1" width="1"/>', '2013-01-04'),
(33, 5, 'The Television Show!', 'http://www.onthemedia.org/2012/dec/28/', 'http://feeds.wnyc.org/~r/onthemedia/~3/D2njzMaWR7k/', 37447403, 'http://feeds.wnyc.org/~r/onthemedia/~5/oXYI8us15iQ/otm122812pod.mp3', 'audio/mpeg', '<p>An update to On the Media''s look at the world of television, originally aired in May, including how the industry is coping with changing consumer habits, the future of the communal viewing experience, and reality TV''s reality problem.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/D2njzMaWR7k" height="1" width="1"/>', '2012-12-28'),
(34, 5, 'The Surprising History of Gun Control, School Shooting Myths, and More', 'http://www.onthemedia.org/2012/dec/21/', 'http://feeds.wnyc.org/~r/onthemedia/~3/zAvKImbU9ls/', 37446971, 'http://feeds.wnyc.org/~r/onthemedia/~5/FrYXXG022sU/otm122112pod.mp3', 'audio/mpeg', '<p>The surprising history of the gun control narrative, the media myths of past school shootings, and the problem when the media speculate on the mental health of shooters.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/zAvKImbU9ls" height="1" width="1"/>', '2012-12-21'),
(35, 5, 'Depictions of Torture in "Zero Dark Thirty," John McAfee''s Media Manipulations, and More', 'http://www.onthemedia.org/2012/dec/14/', 'http://feeds.wnyc.org/~r/onthemedia/~3/_Pi927XjXtU/', 43783547, 'http://feeds.wnyc.org/~r/onthemedia/~5/u614F6g7o6k/otm121412pod.mp3', 'audio/mpeg', '<p>Criticism over "Zero Dark Thirty"''s portrayal of torture, John McAfee''s ability to exploit the press'' fascination with him, and the media errors of the past year.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/_Pi927XjXtU" height="1" width="1"/>', '2012-12-14'),
(36, 5, 'Photographing Tragedy, the Risk of Killer Robots, and More', 'http://www.onthemedia.org/2012/dec/07/', 'http://feeds.wnyc.org/~r/onthemedia/~3/YMh0PsDrrQ0/', 37427098, 'http://feeds.wnyc.org/~r/onthemedia/~5/EzmFhGFPgPo/otm120712pod.mp3', 'audio/mpeg', '<p>The ethics of photographing human tragedy, studying the risk of a robot uprising, and one man''s attempt to be the trendiest guy in New York.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/YMh0PsDrrQ0" height="1" width="1"/>', '2012-12-07'),
(37, 5, 'A Stolen Face, The Leveson Report, and More', 'http://www.onthemedia.org/2012/nov/30/', 'http://feeds.wnyc.org/~r/onthemedia/~3/m_d4KSNl8Zs/', 37426235, 'http://feeds.wnyc.org/~r/onthemedia/~5/79BCcnDbA-I/otm113012pod.mp3', 'audio/mpeg', '<p>A tragic case of mistaken identity, the conclusion of the Leveson Inquiry, and whistleblowers rejoice!</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/m_d4KSNl8Zs" height="1" width="1"/>', '2012-11-30'),
(38, 5, 'Publishing: Adapt or Die', 'http://www.onthemedia.org/2012/nov/23/', 'http://feeds.wnyc.org/~r/onthemedia/~3/i-QIrU9dQKY/', 36365675, 'http://feeds.wnyc.org/~r/onthemedia/~5/MnSYSy5MpIg/otm112312pod.mp3', 'audio/mpeg', '<p>An update of On the Media''s annual look at the publishing industry that originally aired in April, including fears of Amazon becoming a monopoly and the little publishing house standing up to it, a Pulitzer snub for fiction, and the problem of knock-off books.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/i-QIrU9dQKY" height="1" width="1"/>', '2012-11-23'),
(39, 5, 'Seduced by Petraeus, Israel''s Twitter Offensive, and More', 'http://www.onthemedia.org/2012/nov/16/', 'http://feeds.wnyc.org/~r/onthemedia/~3/pdPSR2PAT4o/', 36367403, 'http://feeds.wnyc.org/~r/onthemedia/~5/4VGAgskRbNo/otm111612pod.mp3', 'audio/mpeg', '<p>How journalists were seduced by Petraeus, ten years of sunshine laws in Mexico, and the Israel Defense Forces blogging their assault on Gaza in real time.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/pdPSR2PAT4o" height="1" width="1"/>', '2012-11-16'),
(40, 5, 'Assessing Obama''s First Term, Covering China''s ''Election,'' and More', 'http://www.onthemedia.org/2012/nov/09/', 'http://feeds.wnyc.org/~r/onthemedia/~3/_vsyLHk_xm8/', 36362650, 'http://feeds.wnyc.org/~r/onthemedia/~5/OCa0V7tEHOU/otm110912pod.mp3', 'audio/mpeg', '<p>The absurd exercise of covering China''s election, redefining what it means to be ''online,'' and an OTM report card on Obama''s first four years.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/_vsyLHk_xm8" height="1" width="1"/>', '2012-11-09'),
(41, 5, 'Misinformation Around Sandy, Calling the Election, and More', 'http://www.onthemedia.org/2012/nov/02/', 'http://feeds.wnyc.org/~r/onthemedia/~3/e4Ujtv1h5sA/', 36317141, 'http://feeds.wnyc.org/~r/onthemedia/~5/xzHpmcQMC6o/otm110212pod.mp3', 'audio/mpeg', '<p>Separating fact from fiction during Sandy, calling the presidential election in the final days of the campaign, and why Clark Kent is leaving<em>The Daily Planet.</em></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/e4Ujtv1h5sA" height="1" width="1"/>', '2012-11-02'),
(42, 5, 'The Facebook Show', 'http://www.onthemedia.org/2012/oct/26/', 'http://feeds.wnyc.org/~r/onthemedia/~3/W17lSj5hqwA/', 36360923, 'http://feeds.wnyc.org/~r/onthemedia/~5/XJHl3pOjQnk/otm102612pod.mp3', 'audio/mpeg', '<p>An Austrian man who got Facebook to give him everything they had on him, a writer whose rapist friended her on Facebook, the value of a "Like."</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/W17lSj5hqwA" height="1" width="1"/>', '2012-10-26'),
(43, 5, 'The Taliban''s Media Problem, Ending Newspaper Political Endorsements, and More', 'http://www.onthemedia.org/2012/oct/19/', 'http://feeds.wnyc.org/~r/onthemedia/~3/kooTd7_bP-E/', 36362651, 'http://feeds.wnyc.org/~r/onthemedia/~5/td7KOcHv2-I/otm101912pod.mp3', 'audio/mpeg', '<p>The Taliban''s new war on journalists, ending political endorsements in newspapers, and the potential for the Red Bull Stratos jump to be more than a marketing stunt.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/kooTd7_bP-E" height="1" width="1"/>', '2012-10-19'),
(44, 5, 'All About Elections', 'http://www.onthemedia.org/2012/oct/12/', 'http://feeds.wnyc.org/~r/onthemedia/~3/p0Y4cHkOfgM/', 36371291, 'http://feeds.wnyc.org/~r/onthemedia/~5/PByQmc94Smc/otm101212pod.mp3', 'audio/mpeg', '<p>This week<em>On the Media</em>focuses on elections, including why voters make the decisions they do, and how campaigns try to influence them.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/p0Y4cHkOfgM" height="1" width="1"/>', '2012-10-12'),
(45, 5, 'Anatomy of a Publicity Stunt, The Problem with ''Muslim Rage'', and more', 'http://www.onthemedia.org/2012/oct/05/', 'http://feeds.wnyc.org/~r/onthemedia/~3/oe8u3vHRyH0/', 36366538, 'http://feeds.wnyc.org/~r/onthemedia/~5/sKpqDZ-kqtc/otm100512pod.mp3', 'audio/mpeg', '<p>Breaking down David Blaine''s latest publicity stunt, Nate Silver on predictions, and how the media got the "Muslim Rage" story all wrong.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/oe8u3vHRyH0" height="1" width="1"/>', '2012-10-05'),
(46, 5, 'Russia''s Broadening Definition of Treason, Manipulating Science Journalists, and More', 'http://www.onthemedia.org/2012/sep/28/', 'http://feeds.wnyc.org/~r/onthemedia/~3/T3qphrrtNSU/', 36680603, 'http://feeds.wnyc.org/~r/onthemedia/~5/Nd8gFMGfP1E/otm092812pod.mp3', 'audio/mpeg', '<p>Russia tries to broaden its definition of high treason, one journalist''s quest to get the media to stop referring to immigrants as "illegal," and the upcoming presidential debates.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/T3qphrrtNSU" height="1" width="1"/>', '2012-09-28'),
(47, 5, 'Just The Facts, Please', 'http://www.onthemedia.org/2012/sep/21/', 'http://feeds.wnyc.org/~r/onthemedia/~3/_UWRu2t6Azc/', 36683195, 'http://feeds.wnyc.org/~r/onthemedia/~5/RAdGCAeX5Ro/otm092112pod.mp3', 'audio/mpeg', '<p>This week''s show is dedicated to the search for truth. Or, in journalism terms, fact-checking.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/_UWRu2t6Azc" height="1" width="1"/>', '2012-09-21'),
(48, 5, 'Does NPR Have a Liberal Bias?', 'http://www.onthemedia.org/2012/sep/14/', 'http://feeds.wnyc.org/~r/onthemedia/~3/DC1BP46eq84/', 36371291, 'http://feeds.wnyc.org/~r/onthemedia/~5/dgBAgqYdEBw/otm091412pod.mp3', 'audio/mpeg', '<p>This week, a full hour of highlights from our exploration of liberal bias and public media, which we conducted in March of 2011. Brooke talks to NPR listeners, pollsters, media watchers, and This American Life''s Ira Glass in search of an answer to the question: does NPR have a liberal bias?</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/DC1BP46eq84" height="1" width="1"/>', '2012-09-14'),
(49, 5, 'Covering Conventions, the Legacy of Sun Myung Moon, and More', 'http://www.onthemedia.org/2012/sep/07/', 'http://feeds.wnyc.org/~r/onthemedia/~3/RtzpnJpxxpw/', 36357034, 'http://feeds.wnyc.org/~r/onthemedia/~5/-x-BQAY3Ors/otm090712pod.mp3', 'audio/mpeg', '<p>Covering the totally predictable conventions, why political journalists are tired of this campaign season, and a service that gauges the hostility of your emails.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/RtzpnJpxxpw" height="1" width="1"/>', '2012-09-07'),
(50, 5, 'Fact-Checkers in the Spotlight, Why Nigerian Prince Scams Work, And More.', 'http://www.onthemedia.org/2012/aug/31/', 'http://feeds.wnyc.org/~r/onthemedia/~3/uPqrsxRlikk/', 36371291, 'http://feeds.wnyc.org/~r/onthemedia/~5/FgV7sK8fD3E/otm083112pod.mp3', 'audio/mpeg', '<p>Fact-checkers, and facts, become the targets of criticism, the reason why scammers tell you they''re from Nigeria, and Florida''s weirdness explained.</p><p></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/uPqrsxRlikk" height="1" width="1"/>', '2012-08-31'),
(51, 5, 'No More Tailoring Political Messages, Communicating the Libyan Revolution, and More', 'http://www.onthemedia.org/2012/aug/24/', 'http://feeds.wnyc.org/~r/onthemedia/~3/pdMrAaT9Cbk/', 36371291, 'http://feeds.wnyc.org/~r/onthemedia/~5/pvOiwfxTQ2U/otm082412pod.mp3', 'audio/mpeg', '<p>The end of politicians tailoring their messages based on audience, license plate tracking nationwide, and following up with the people communicating the Libyan revolution one year later.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/pdMrAaT9Cbk" height="1" width="1"/>', '2012-08-24'),
(52, 5, 'Ayn Rand''s Political Influence, Presidential Political Ad Season, and More', 'http://www.onthemedia.org/2012/aug/17/', 'http://feeds.wnyc.org/~r/onthemedia/~3/27_wbjQF9OE/', 36371290, 'http://feeds.wnyc.org/~r/onthemedia/~5/vYFKj52uP6w/otm081712pod.mp3', 'audio/mpeg', '<p>Talking about Ayn Rand''s influence on Paul Ryan and the political landscape at large, half-truths and outright lies in this season''s political advertising, remembering<em>Cosmopolitan</em>editor Helen Gurley Brown, and a website that tracks all the homicides in Washington D.C.<br><br></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/27_wbjQF9OE" height="1" width="1"/>', '2012-08-17'),
(53, 5, 'Losing Everything to Hackers, the Pussy Riot Trial and More', 'http://www.onthemedia.org/2012/aug/10/', 'http://feeds.wnyc.org/~r/onthemedia/~3/UrXKBW7jFIM/', 36367403, 'http://feeds.wnyc.org/~r/onthemedia/~5/ffxDsEzUsBo/otm081012pod.mp3', 'audio/mpeg', '<p>How one journalist lost his digital life to hackers, how worried you should be about Cyberwar, and the fascinating trial of punk band Pussy Riot in Russia.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/UrXKBW7jFIM" height="1" width="1"/>', '2012-08-10'),
(54, 5, 'NBC''s Olympics Woes, Posthumous Outing, and More', 'http://www.onthemedia.org/2012/aug/03/', 'http://feeds.wnyc.org/~r/onthemedia/~3/lMy1LtyTabs/', 36366538, 'http://feeds.wnyc.org/~r/onthemedia/~5/Gx25IENz3l4/otm080312pod.mp3', 'audio/mpeg', '<p>Criticism for how NBC is broadcasting the Olympics, the difference between venture capital and private equity, and revealing a public person''s sexual orientation after death.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/lMy1LtyTabs" height="1" width="1"/>', '2012-08-03'),
(55, 5, 'Olympic Branding Police, Confessions of a Media Manipulator, and More', 'http://www.onthemedia.org/2012/jul/27/', 'http://feeds.wnyc.org/~r/onthemedia/~3/pxpSkEcWdH4/', 36366539, 'http://feeds.wnyc.org/~r/onthemedia/~5/5vemltlzO1g/otm072712pod.mp3', 'audio/mpeg', '<p>Syrian rebels appealing to donors through videos online, extreme measures to protect Olympic sponsorships, and a self proclaimed media manipulator tells all.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/pxpSkEcWdH4" height="1" width="1"/>', '2012-07-27'),
(56, 5, 'Richard Nixon: Transparency Champion, Endangered Sounds, and more', 'http://www.onthemedia.org/2012/jul/20/', 'http://feeds.wnyc.org/~r/onthemedia/~3/DtI6pv4Dieo/', 36370859, 'http://feeds.wnyc.org/~r/onthemedia/~5/DcaNfis8Es4/otm072012pod.mp3', 'audio/mpeg', '<p>The death of the Disclose Act in the Senate, journalists getting quote approval from presidential campaigns before publication, an app that identifies the organizations behind political ads using sound alone, and an online museum that preserves endangered sounds.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/DtI6pv4Dieo" height="1" width="1"/>', '2012-07-20'),
(57, 5, 'Romney''s Bain Departure Date, E-books That Read You, And More...', 'http://www.onthemedia.org/2012/jul/13/', 'http://feeds.wnyc.org/~r/onthemedia/~3/sryeySpZ160/', 36371291, 'http://feeds.wnyc.org/~r/onthemedia/~5/M5EsmuNw3vA/otm071312pod.mp3', 'audio/mpeg', '<p><span>A website that lets you argue with a complete stranger about politics over the phone, how online books sellers can monitor you reading a book WHILE you read it, and Bob examines his practice of binge-watching TV shows.<br></span></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/sryeySpZ160" height="1" width="1"/>', '2012-07-13'),
(58, 5, 'CNN''s Big Mistake, Buzzfeed''s Big Success and More', 'http://www.onthemedia.org/2012/jul/06/', 'http://feeds.wnyc.org/~r/onthemedia/~3/ILbCVPHGTSQ/', 36371290, 'http://feeds.wnyc.org/~r/onthemedia/~5/Qumwj1TgTgk/otm070612pod.mp3', 'audio/mpeg', '<p><span>Bob looks at why some news organizations find it more important to be first than to be right, the benefits and risks of storing information in the ''cloud,'' and the secret to Buzzfeed''s success. Also, the history of the TV pitchman.</span></p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/ILbCVPHGTSQ" height="1" width="1"/>', '2012-07-06'),
(59, 5, 'The Data Show', 'http://www.onthemedia.org/2012/jun/29/', 'http://feeds.wnyc.org/~r/onthemedia/~3/mnmkAbCIu7Q/', 37245479, 'http://feeds.wnyc.org/~r/onthemedia/~5/IOjMazxMzQ8/otm062912pod.mp3', 'audio/mpeg', '<p>Data. We.re awash in it, we make it, we save it, computers crunch it at an unprecedented rate. Radiolab.s Jad Abumrad talks with Brooke about how data inform us and can lead us astray.</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/mnmkAbCIu7Q" height="1" width="1"/>', '2012-06-29'),
(60, 5, 'Mexican Media: Es Muy Complicado', 'http://www.onthemedia.org/2012/jun/22/', 'http://feeds.wnyc.org/~r/onthemedia/~3/vaB5rv4Jdtw/', 36369563, 'http://feeds.wnyc.org/~r/onthemedia/~5/uKtUW2oJaxM/otm062212pod.mp3', 'audio/mpeg', '<p>This week, Brooke is joined by WNYC reporter Marianne McCune as OTM reports from Mexico! This hour features stories on the relationship between media and politics.and the youth-led movement that''s fed up with it; the difficulties for journalists reporting in areas run by the drug cartels; and the image problem Mexico faces on the international stage. As we heard time and again while in Mexico: when it comes to the Mexican media, "it''s very complicated."</p><img src="http://feeds.feedburner.com/~r/onthemedia/~4/vaB5rv4Jdtw" height="1" width="1"/>', '2012-06-22'),
(61, 6, '#462: When Patents Hit the Podcast', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/187684278/npr_187684278.mp3', 'http://www.npr.org/planetmoney', 11491248, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/187684278/npr_187684278.mp3', 'audio/mpeg', '<p>Back in the nineties, Jim Logan started a company called Personal Audio. The concept was simple &#8212; people could pick out magazine articles they liked on the internet, and his company would send them a cassette tape of those articles being read out loud. The cassette tapes didn''t catch on like Jim hoped, but he had bigger dreams for the idea behind them.</p><p>He dreamed that one day you wouldn''t need a cassette player, you would just be able to hear smart people talking about whatever subject you wanted, and that audio would be magically downloaded to a device of your choice. He says he dreamed of podcasting as we know it today.</p><p>Now Jim Logan did not create the technology to podcast. He himself is not a modern-day podcaster. But he did get a patent on that big dream of downloading personalized audio, and he claims to have the patent on podcasting.</p><p>On today''s show, he says all the people out there podcasting today, owe him money.</p><p><strong>For more</strong>, check out Alex Blumberg and Laura Sydell''s story on this weekend''s This American Life :<a href="http://www.thisamericanlife.org/radio-archives/episode/496/when-patents-attackpart-two">When Patents Attack...Part Two!</a></p>', '2013-05-31'),
(62, 6, '#461: Lawyers, Guns and Money', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/186972435/npr_186972435.mp3', 'http://www.npr.org/planetmoney', 7930444, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/186972435/npr_186972435.mp3', 'audio/mpeg', '<p>On today''s show: Three short stories from the far flung shores of New Zealand, Ireland and New Jersey.</p><p>First up, it''s no secret that some Americans hide money offshore to avoid paying U.S. taxes. Over the past decade, some 39,000 people have come forward voluntarily to tell the IRS about their offshore money. This group provides a small window into the world of people who are hiding money in offshore havens.</p><p>Also: how a single page in a report written decades ago by U.S. consultants, and funded by the U.S. State Department, brought Apple to Ireland.</p><p></p>', '2013-05-28'),
(63, 6, '#217: The Art Of Living At The Poverty Line', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/186506124/npr_186506124.mp3', 'http://www.npr.org/planetmoney', 11225008, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/186506124/npr_186506124.mp3', 'audio/mpeg', '<p style="font-size: 14px; margin: 0px 0px 10px;">On today''s Planet Money, we meet a single mother who makes $16,000 a year &#8212; and who managed to fund a vacation at a Caribbean resort with an interest-free loan from one of the world''s largest banks.</p><p style="font-size: 14px; margin: 0px 0px 10px;">Edith Calzado gets credit cards with teaser zero-percent interest rates &#8212; then transfers her balance before the rate ticks up. She signs up for store cards to get discounts &#8212; then pays off her bill on time. She gets food stamps and lives in subsidized housing. Her son is doing well in school.</p><p style="font-size: 14px; margin: 0px 0px 10px;">She may be the single most successful and productive beneficiary of government assistance you''ll ever meet.</p><p style="font-size: 14px; margin: 0px 0px 10px;"><em>Note: This podcast was originally posted in 2010.&#160;</em></p>', '2013-05-24'),
(64, 6, '#460: It''s Hard To Do Good', 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/185871614/npr_185871614.mp3', 'http://www.npr.org/planetmoney', 11538268, 'http://podcastdownload.npr.org/anon.npr-podcasts/podcast/510289/185871614/npr_185871614.mp3', 'audio/mpeg', '<p style="font-size: 14px; margin: 0px 0px 10px;">In 2010,&#160;<a href="http://www.npr.org/blogs/money/2010/06/10/127750586/how-foreign-aid-is-hurting-haitian-farmers" target="_blank">we reported on a poor town in Haiti</a>, where school was held in a small, one-room church.</p><p style="font-size: 14px; margin: 0px 0px 10px;">Planet Money listeners were moved to donate some $3,000, which the principal of the school thought would be enough to build a school. A few months later, the money was gone, and all there was to show for it was&#160;<a href="http://www.npr.org/blogs/money/2010/11/30/131705055/the-tuesday-podcast-what-your-3-000-bought-in-haiti" target="_blank">a foundation, some concrete blocks and some rock and sand</a>.</p><p style="font-size: 14px; margin: 0px 0px 10px;">We thought that would be the end of it. Then we heard from Tim Myers, a retired contractor from Colorado who decided to go to Haiti to build the school &#8212; and who realized, in 2011, that&#160;<a href="http://www.npr.org/blogs/money/2011/08/09/139237936/new-estimate-to-build-a-school-in-haiti-143-900" target="_blank">the project would cost more than $100,000</a>.</p><p style="font-size: 14px; margin: 0px 0px 10px;">On today''s show: We return to Haiti, to see how the project is going. And we hear from Tim Myers, who says, if he had it to do over, he might do things differently.</p>', '2013-05-21');

