INSERT INTO `PodCasts_podcast` (`id`, `Title`, `Link`, `ImageLink`, `Description`, `LastRetrived`) VALUES
(1, 'NPR Programs: Wait Wait... Don''t Tell Me! Podcast', 'http://www.npr.org/templates/rss/podlayer.php?id=35', 'http://media.npr.org/images/podcasts/primary/npr_wwdtm_image_300.jpg', 'NPR''s weekly current events quiz.  Have a laugh and test your news knowledge while figuring out what''s real and what we''ve made up.', '2013-06-16 20:43:22'),
(2, 'The Moth Podcast', 'http://feeds.themoth.org/themothpodcast', 'http://cdn.themoth.prx.org/moth_podcast_1400x1400.jpg', 'Since its launch in 1997, The Moth has presented thousands of true stories, told live and without notes, to standing-room-only crowds worldwide. Moth storytellers stand alone, under a spotlight, with only a microphone and a roomful of strangers. The storyteller and the audience embark on a high-wire act of shared experience which is both terrifying and exhilarating. Since 2008, The Moth podcast has featured many of our favorite stories told live on Moth stages around the country. For information on all of our programs and live events, visit themoth.org.', '2013-06-16 21:48:07'),
(3, 'This American Life', 'http://feeds.thisamericanlife.org/talpodcast', 'http://www.thisamericanlife.org/sites/all/themes/thislife/images/logo-square-1400.jpg', 'Official free, weekly podcast of the award-winning radio show "This American Life." First-person stories and short fiction pieces that are touching, funny, and surprising. Hosted by Ira Glass, from WBEZ Chicago Public Radio. In mp3 and updated Mondays.', '2013-06-16 19:33:28'),
(4, 'PRI: To the Best of our Knowledge', 'http://feeds.feedburner.com/ttbook', 'http://wpr.org/podcasts/images/ttbook_300x300.jpg', 'To the Best of Our Knowledge cracks open the world and the ideas that fuel it through interviews with the world''s luminaries, from experts to cultural icons.  Each show revolves around a theme where we explore these ideas and the people who consider them.', '2013-06-03 02:53:29'),
(5, 'On the Media', 'http://feeds.wnyc.org/onthemedia', 'http://media40.wnyc.net/media/photologue/photos/OTM_logo_RGB.png', 'The smartest, wittiest, most incisive media analysis show in the universe. The weekly one-hour podcast of NPR.s On the Media is your guide to how the media sausage is made.  Hosts Brooke Gladstone and Bob Garfield examine threats to free speech and government transparency, criticize media coverage of the week.s big stories, examine new technology, and unravel hidden political narratives in the media. In an age of information overload, OTM helps you dig your way out. The Peabody Award winning show is produced by WNYC Radio.', '2013-06-02 23:45:04'),
(6, 'NPR: Planet Money Podcast', 'http://www.npr.org/rss/podcast.php?id=510289', 'http://media.npr.org/images/podcasts/2013/primary/planet_money.png', 'Money makes the world go around, faster and faster every day. On NPR''s Planet Money, you''ll meet high rollers, brainy economists and regular folks -- all trying to make sense of our rapidly changing global economy.', '2013-06-17 00:21:09');
